window.onscroll=function(){const e=document.querySelector("header"),n=e.offsetTop;window.pageYOffset>n?e.classList.add("navbar-fixed"):e.classList.remove("navbar-fixed")};const hamburger=document.querySelector("#hamburger"),navMenu=document.querySelector("#nav-menu");hamburger.addEventListener("click",function(){hamburger.classList.toggle("hamburger-active"),navMenu.classList.toggle("hidden")});